import React, { useState, useEffect } from 'react';
import axios from 'axios';

function News() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        const fetchNews = async () => {
            const config = {
                headers: {
                    'X-Naver-Client-Id': process.env.REACT_APP_NAVER_CLIENT_ID,
                    'X-Naver-Client-Secret': process.env.REACT_APP_NAVER_CLIENT_SECRET
                }
            };
            try {
                const response = await axios.get("https://openapi.naver.com/v1/search/news.json?query=React", config);
                setArticles(response.data.items);
            } catch (error) {
                console.error('뉴스를 불러오는 데 실패했습니다', error);
            }
        };

        fetchNews();
    }, []);

    return (
        <div>
            <h1>뉴스 기사</h1>
            <ul>
                {articles.map(article => (
                    <li key={article.link}>
                        <a href={article.link} target="_blank" rel="noopener noreferrer">{article.title.replace(/<[^>]+>/g, '')}</a>
                        <p>{article.description.replace(/<[^>]+>/g, '')}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default News;
